import React, { useState } from 'react'
import data from './info.json'

const initial=data;

const Main = () => {
    const [jsonData, setJsonData] = useState(initial)
    const deleteItemById = (id) => {
        setJsonData(prevData => prevData.filter(user => user.id !== id));
      };
  return (
    <>
        <h1>Admin Dashboard</h1>
        <input type="text" id="searchInput" placeholder="Search..." oninput="filterTable()" />
        <table id="userTable">
        <thead>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>

            {jsonData.map(user=>(
                <tr key={user.id}>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.role}</td>
                    <td>
                        <button onClick={()=>{alert("Edit")}}>Edit</button>
                        <button onClick={() => deleteItemById(user.id)}>Delete</button>
                    </td>
                </tr>
            ))}

        </tbody>
    </table>
    <button onclick="deleteSelected()">Delete Selected</button>
    </>
  )
}

export default Main
