import React from 'react'
import d from './info.json'

const Main = () => {
    let data = d;
  return (
    <>
        <h1>Admin Dashboard</h1>
        <input type="text" id="searchInput" placeholder="Search..." oninput="filterTable()" />
        <table id="userTable">
        <thead>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>

            {data.map(user=>(
                <tr>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.role}</td>
                    <td>
                        <button onClick={()=>{alert("Edit")}}>Edit</button>
                        <button onClick={()=>{
                            let index = parseInt(user.id);
                            // console.log(index);
                            if (index !== -1) {
                                data.splice(index, 1);
                            }
                            }}>Delete</button>
                    </td>
                </tr>
            ))}

        </tbody>
    </table>
    <button onclick="deleteSelected()">Delete Selected</button>
    </>
  )
}

export default Main
