import React, { useState } from 'react'
import data from './info.json'

const initial=data;

const Main = () => {

    const itemsPerPage = 10;

    const [jsonData, setJsonData] = useState(initial);
    const [editableId, setEditableId] = useState(null);
    const [editedData, setEditedData] = useState({ name: '', email: '',role: ''});
    const [searchTerm, setSearchTerm] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [selectedRows, setSelectedRows] = useState([]);
    const [selectAll, setSelectAll] = useState(false);

    const deleteItemById = (id) => {
        setJsonData(prevData => prevData.filter(item => item.id !== id));
      };

      const startEditing = (id, name, email, role) => {
        setEditableId(id);
        setEditedData({ name, email, role });
      };

      const cancelEditing = () => {
        setEditableId(null);
        setEditedData({ name: '', email: '',role:'' });
      };

      const saveEditing = (id) => {
        setJsonData(prevData =>
          prevData.map(item =>
            item.id === id
              ? { ...item, name: editedData.name, email: editedData.email, role:editedData.role }
              : item
          )
        );
        setEditableId(null);
        setEditedData({ name: '', email: '',role:'' });
      };

      const handleSearch = (e) => {
        setSearchTerm(e.target.value);
        setCurrentPage(1); // Reset to first page when searching
      };

      const handleRowSelection = (id) => {
        setSelectedRows(prevSelectedRows => {
          if (prevSelectedRows.includes(id)) {
            // Deselect the row if it's already selected
            return prevSelectedRows.filter(rowId => rowId !== id);
          } else {
            // Select the row if it's not selected
            return [...prevSelectedRows, id];
          }
        });
      };

      const handlePageChange = (newPage) => {
        if (newPage >= 1 && newPage <= totalPages) {
          setCurrentPage(newPage);
        }
      };

      const deleteSelectedRows = () => {
        setJsonData(prevData => prevData.filter(item => !selectedRows.includes(item.id)));
        setSelectedRows([]);
      };

      const filteredData = jsonData.filter(item =>
        Object.values(item).some(prop =>
          typeof prop === 'string' && prop.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );

      const totalPages = Math.ceil(filteredData.length / itemsPerPage);

      const visibleData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);



    return (
        <>
            <h1>Admin Dashboard</h1>

            <input
                type="text"
                placeholder="Search"
                value={searchTerm}
                onChange={handleSearch}
            />

            <table id="userTable">
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Role</th>
                    <th>Actions</th>
                    <th>
                    <input
                        type="checkbox"
                        checked={selectAll}
                        onChange={handleSelectAll}
                    />
                    </th>
                </tr>
            </thead>
            <tbody>


                {visibleData.map(user=>(
                    <tr key={user.id}
              style={{ backgroundColor: selectedRows.includes(user.id) ? '#ddd' : 'inherit' }}
            >
                        <td>
                        {editableId === user.id ? (
                            <input
                            type="text"
                            value={editedData.name}
                            onChange={(e) => setEditedData({ ...editedData, name: e.target.value })}
                            />
                        ) : (
                            user.name
                        )}
                        </td>
                        <td>
                        {editableId === user.id ? (
                            <input
                            type="text"
                            value={editedData.email}
                            onChange={(e) => setEditedData({ ...editedData, email: e.target.value })}
                            />
                        ) : (
                            user.email
                        )}
                        </td>
                        <td>
                        {editableId === user.id ? (
                            <input
                            type="text"
                            value={editedData.role}
                            onChange={(e) => setEditedData({ ...editedData, role: e.target.value })}
                            />
                        ) : (
                            user.role
                        )}</td>
                        <td>
                        {editableId === user.id ? (
                            <>
                            <button onClick={() => saveEditing(user.id)}>Save</button>
                            <button onClick={cancelEditing}>Cancel</button>
                            </>
                        ) : (
                            <>
                            <button onClick={() => startEditing(user.id, user.name, user.email, user.role)}>Edit</button>
                            <button onClick={() => deleteItemById(user.id)}>Delete</button>
                            </>
                        )}
                        </td>
                        <td>
                            <input
                            type="checkbox"
                            checked={selectedRows.includes(user.id)}
                            onChange={() => handleRowSelection(user.id)}
                            />
                        </td>
                    </tr>
                ))}

            </tbody>
        </table>

        <div>
          <button onClick={deleteSelectedRows} disabled={selectedRows.length === 0}>
            Delete Selected
          </button>
        </div>

        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px' }}>
        <button onClick={() => handlePageChange(1)}>First Page</button>
        <button onClick={() => handlePageChange(currentPage - 1)}>Previous Page</button>
        <div style={{ overflowX: 'auto', display: 'flex' }}>
          {[...Array(totalPages)].map((_, index) => (
            <button
              key={index + 1}
              onClick={() => handlePageChange(index + 1)}
              style={{ margin: '0 5px', fontWeight: currentPage === index + 1 ? 'bold' : 'normal' }}
            >
              {index + 1}
            </button>
          ))}
        </div>
        <button onClick={() => handlePageChange(currentPage + 1)}>Next Page</button>
        <button onClick={() => handlePageChange(totalPages)}>Last Page</button>
      </div>

        </>
    )
}

export default Main
